CREATE TABLE "Users" (
  "id" INT GENERATED BY DEFAULT AS IDENTITY PRIMARY KEY,
  "name" varchar(30) UNIQUE NOT NULL,
  "email" varchar(30) UNIQUE NOT NULL,
  "password" varchar(30) NOT NULL,
  "age" int NOT NULL
);

CREATE TABLE "Cursos" (
  "id" INT GENERATED BY DEFAULT AS IDENTITY PRIMARY KEY,
  "title" int NOT NULL,
  "level" int NOT NULL,
  "profesor" int NOT NULL
);

CREATE TABLE "Cursos_description" (
  "id" INT GENERATED BY DEFAULT AS IDENTITY PRIMARY KEY,
  "title" int NOT NULL,
  "description" varchar(100) UNIQUE NOT NULL,
  "categorie" int NOT NULL
);

CREATE TABLE "Levels" (
  "id" INT GENERATED BY DEFAULT AS IDENTITY PRIMARY KEY,
  "level" varchar(30) UNIQUE NOT NULL
);

CREATE TABLE "videos" (
  "id" INT GENERATED BY DEFAULT AS IDENTITY PRIMARY KEY,
  "title" varchar(30) NOT NULL,
  "url" varchar(100) NOT NULL
);

CREATE TABLE "Categories" (
  "id" INT GENERATED BY DEFAULT AS IDENTITY PRIMARY KEY,
  "categorie" varchar(30) UNIQUE NOT NULL
);

CREATE TABLE "Roles" (
  "id" INT GENERATED BY DEFAULT AS IDENTITY PRIMARY KEY,
  "name" int NOT NULL,
  "rol" int NOT NULL
);

CREATE TABLE "allRoles" (
  "id" INT GENERATED BY DEFAULT AS IDENTITY PRIMARY KEY,
  "rol" varchar(30) UNIQUE NOT NULL
);

ALTER TABLE "Cursos" ADD FOREIGN KEY ("title") REFERENCES "Cursos_description" ("id");

ALTER TABLE "Cursos" ADD FOREIGN KEY ("level") REFERENCES "Levels" ("id");

ALTER TABLE "Cursos" ADD FOREIGN KEY ("profesor") REFERENCES "Roles" ("id");

ALTER TABLE "Cursos_description" ADD FOREIGN KEY ("categorie") REFERENCES "Categories" ("id");

ALTER TABLE "Roles" ADD FOREIGN KEY ("name") REFERENCES "Users" ("id");

ALTER TABLE "Roles" ADD FOREIGN KEY ("rol") REFERENCES "allRoles" ("id");

ALTER TABLE "Cursos_description" ADD FOREIGN KEY ("title") REFERENCES "videos" ("id");

